// Force domain check to always pass
const domainOk = true; // force OK
if (!domainOk) {
    // Original redirect code will never run
    // (this entire block is now effectively disabled)
}

// Keep the window focused and prevent default keys
window.focus();

window.addEventListener("load", function() {
    window.focus();
    document.body.addEventListener("click", function(e) {
        window.focus();
    }, false);
});

window.addEventListener("keydown", function(e) {
    // Prevent default for these keys only
    const keysToPrevent = ["Tab","Space","ArrowUp","ArrowDown","ArrowLeft","ArrowRight"];
    if (keysToPrevent.indexOf(e.code) > -1) {
        e.preventDefault();
    }
}, false);

